import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { View, Text,FlatList } from "react-native";
import {BASE_URL}  from '../../constants/settings';
import {homeapi}  from '../../api/home';


export default function Home() {
  const [productDataList, productData] = useState([]);
  const getProductList = async () => {
    try {
     const response = await Axios.get(`${BASE_URL}/product_list`);
    //  console.log(response.data.data);
    productData(response.data.data);
   }
    catch (error) {
     console.error(error);
   }
  }
  useEffect(() => {
    getProductList();
  }, []);
  

  return (
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Text style={{fontSize:16,fontWeight:'700'}}>Home Screen</Text>
        <Text style={{fontSize:16,fontWeight:'700'}}>{BASE_URL}</Text>
        <FlatList
          data={productDataList}
          keyExtractor={({ id }, index) => id}
          renderItem={({ item }) => (
            <Text>{item.product_name}, {item.product_name}</Text>
          )}
        />

      </View>
    );
  }
