import React, { useEffect, useState, Component, useRef } from 'react';
import Axios from 'axios';
import {
  View,
  Text,
  FlatList,
  ActivityIndicator,
  Button,
  Image,
} from 'react-native';
import { TextInput } from 'react-native-paper';

import { BASE_URL, COLORS } from '../../constants/settings';
import ApiContainer from '../../api/ApiContainer';
import ChildClass from './child';

// const Home = (props) => {
export default function Home(props) {
  const fref = useRef();
  const [text, setText] = useState('');

  let imagePath = {
    uri: 'https://igtechservices.com/public/uploads/setting/1631384125-logo-ig.png',
  };

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text style={{ fontSize: 16, fontWeight: '700' }}>Home Screen</Text>
      <Text style={{ fontSize: 16, fontWeight: '700' }}>{BASE_URL}</Text>
      <Text style={{ fontSize: 16, fontWeight: '700' }}>{COLORS.white}</Text>

      <TextInput
        label='Email'
        value={text}
        onChangeText={(text) => setText(text)}
      />
      <ChildClass name='Max' ref={fref} />
      <Image source={imagePath} style={{ width: 250, height: 250 }} />
      <Button title={'Click using Fetch'} onPress={goForAxios} color='green' />
      <ActivityIndicator />

      {/* <FlatList
          data={productDataList}
          keyExtractor={({ id }, index) => id}
          renderItem={({ item }) => (
            <Text>{item.product_name}, {item.product_name}</Text>
          )}
        /> */}
    </View>
  );
}

