import React, { useEffect, useState, Component, useRef } from 'react';
import Axios from 'axios';
import {
  View,
  Text,
  FlatList,
  ActivityIndicator,
  Button,
  StyleSheet,
  Image,
} from 'react-native';
import { TextInput } from 'react-native-paper';
import { BASE_URL, COLORS } from '../../constants/settings';
import Carousel from 'react-native-snap-carousel';
import CarouselCardItem, { SLIDER_WIDTH, ITEM_WIDTH } from './CarouselCardItem';
export default function Home(props) {
  const isCarousel = React.useRef(null);
  const data = [
    {
      title: 'Aenean leo',
      body: 'Ut tincidunt tincidunt erat. Sed cursus turpis vitae tortor. Quisque malesuada placerat nisl. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem.',
      imgUrl: 'https://picsum.photos/id/11/200/300',
    },
    {
      title: 'In turpis',
      body: 'Aenean ut eros et nisl sagittis vestibulum. Donec posuere vulputate arcu. Proin faucibus arcu quis ante. Curabitur at lacus ac velit ornare lobortis. ',
      imgUrl: 'https://picsum.photos/id/10/200/300',
    },
    {
      title: 'Lorem Ipsum',
      body: 'Phasellus ullamcorper ipsum rutrum nunc. Nullam quis ante. Etiam ultricies nisi vel augue. Aenean tellus metus, bibendum sed, posuere ac, mattis non, nunc.',
      imgUrl: 'https://picsum.photos/id/12/200/300',
    },
  ];
  const fref = useRef();
  const [text, setText] = useState('');
  const [productDataList, productData] = useState([]);
  const getProductList = async () => {
    try {
      const response = await Axios.get(`${BASE_URL}/product_list`);
      productData(response.data.data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {}, []);
  const fetchapi = () => {};

  let imagePath = {
    uri: 'https://igtechservices.com/public/uploads/setting/1631384125-logo-ig.png',
  };

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 16, fontWeight: '700' }}>Home Screen</Text>

      {/* <FlatList
        data={productDataList}
        keyExtractor={({ id }, index) => id}
        renderItem={({ item }) => (
          <Text>
            {item.product_name}, {item.product_name}
          </Text>
        )}
      /> */}
      <Carousel
        layout='tinder'
        layoutCardOffset={9}
        ref={isCarousel}
        data={data}
        renderItem={CarouselCardItem}
        sliderWidth={SLIDER_WIDTH}
        itemWidth={ITEM_WIDTH}
        inactiveSlideShift={0}
        useScrollView={true}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },
});
