import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import {BASE_URL}  from '../constants/settings';

const Child = forwardRef((props, ref) => {
  useImperativeHandle(
      ref,
      () => ({
          showAlert() {
              alert("Child function called")
          }
       }),
   )
   return (
       <div>Child Component</div>
   )
})
