import React from 'react';
import { View, Text, StyleSheet, Dimensions, Image } from 'react-native';

export const { width, height } = Dimensions.get('window');
export const ITEM_WIDTH = Math.round(width * 0.7);

const CarouselCardItem = ({ item, index }) => {
  // console.log({ item });
  return (
    <>
      <View style={styles.container} key={index}>
        {item?.map((list) => {
          return (
            <>
              <Image
                source={{ uri: 'https://themaxhype.com/' + list.imgUrl }}
                style={styles.image}
              />
              <View style={styles.mainContent}>
                <Text style={styles.body}> {list.title} </Text>
                <Text style={styles.subtext}> {list.body} </Text>
              </View>
            </>
          );
        })}
      </View>
    </>
  );
};
const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    borderRadius: 8,
    width: width,
    // height: height,
    // paddingBottom: 40,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.29,
    shadowRadius: 4.65,
    elevation: 7,
    opacity: 0.5,
  },
  image: {
    width: width,
    height: 300,
    // borderRadius: 10,
    // opacity: 0.8,
  },
  header: {
    color: '#222',
    fontSize: 28,
    paddingLeft: 20,
    paddingTop: 20,
  },
  mainContent: {
    color: '#ffff',
    alignItems: 'center',
  },
  body: {
    color: '#a3d8f1',
    position: 'absolute',
    // justifyContent: 'center',
    top: -200,
    fontSize: 18,
  },
  subtext: {
    color: '#fff',
    // left: '40%',
    fontWeight: 'bold',

    top: -150,
    position: 'absolute',
    fontSize: 30,
  },
});

export default CarouselCardItem;
